    return fetch(`/CONTENTLISTINGPAGE-PAGE${ind}.json`)
  }

  fetchNextData(1).then(d => console.log(d))

  useEffect()

  return (
    <div className="main">
      <InfiniteScroll
        hasChildren={true}
        hasMore={false}
        loader={
          <div className="scroll-loader">
            <Loader type="ThreeDots" color="#68edd4" height={80} width={80} />
          </div>
        }
        endMessage={
          <p>
            <b>You've seen it all</b>
          </p>
        }
      >
        <div className="flex flex-wrap">
          {MovieList.page['content-items'].content.map((item, idx) => (
            <div key={idx} className="w-1/3 h-12">
              <img
                key={idx}
                src={require(`../../Slices/${item['poster-image']}`)}
                alt={item.name}
              />
              <span className="listing-item-name">{item.name}</span>
            </div>
          ))}
        </div>
      </InfiniteScroll>
    </div>
  )
}

const mapStateToProps = state => {
  return {
    idx: state.listing.idx,
    listingData: state.listing.listingData,
    hasMore: state.listing.hasMore,
  }
}

const mapDispatchToProps = {
  setListingData,
}

export default connect(
  mapStateToProps,
  mapDispatchToProps,
)(ListingPage)
