import React from 'react'
import './ListingPage.css'
import MovieList from '../../API/CONTENTLISTINGPAGE-PAGE1.json'
import Loader from 'react-loader-spinner'
import InfiniteScroll from 'react-infinite-scroll-component'
import SecondMovieList from '../../API/CONTENTLISTINGPAGE-PAGE2.json'

window.sm = SecondMovieList
// const fetchNextData = () => {
//   return fetch('../../API/CONTENTLISTINGPAGE-PAGE1.json')
// }

async function loadJSON(url) {
  const res = await fetch(url)
  return await res
}

loadJSON('../../API/CONTENTLISTINGPAGE-PAGE1.json').then(data => {
  console.log(data.page)
})

// fetchNextData().then(d => console.log(d))

export default function ListingPage() {
  return (
    <div className="main">
      <InfiniteScroll
        hasChildren={true}
        hasMore={true}
        loader={
          <div className="scroll-loader">
            <Loader type="ThreeDots" color="#68edd4" height={80} width={80} />
          </div>
        }
        endMessage={
          <p>
            <b>You've seen it all</b>
          </p>
        }
      >
        <div className="flex flex-wrap">
          {MovieList.page['content-items'].content.map((item, idx) => (
            <div key={idx} className="w-1/3 h-12">
              <img
                key={idx}
                src={require(`../../Slices/${item['poster-image']}`)}
                alt={item.name}
              />
              <span className="listing-item-name">{item.name}</span>
            </div>
          ))}
        </div>
      </InfiniteScroll>
    </div>
  )
}
