import React, { useState, useEffect } from 'react'
import axios from 'axios'
import './ListingPage.css'
import MovieList from './CONTENTLISTINGPAGE-PAGE1.json'
import Loader from 'react-loader-spinner'
import InfiniteScroll from 'react-infinite-scroll-component'
import { connect } from 'react-redux'
import { setListingData } from '../../modules/listingReducer'

const ListingPage = ({ setListingData }) => {
  const fetchNextData = () => {
    return axios(`/CONTENTLISTINGPAGE-PAGE1.json`)
  }

  const respone = fetchNextData().then(res =>
    s(res.data.page['content-items'].content),
  )

  useEffect(
    () =>
      fetchNextData().then(res =>
        setListingData(res.data.page['content-items'].content),
      ),
    [],
  )

  return (
    <div className="main">
      <InfiniteScroll
        hasChildren={true}
        hasMore={false}
        loader={
          <div className="scroll-loader">
            <Loader type="ThreeDots" color="#68edd4" height={80} width={80} />
          </div>
        }
        endMessage={
          <p>
            <b>You've seen it all</b>
          </p>
        }
      >
        <div className="flex flex-wrap">
          {MovieList.page['content-items'].content.map((item, idx) => (
            <div key={idx} className="w-1/3 h-12">
              <img
                key={idx}
                src={require(`../../Slices/${item['poster-image']}`)}
                alt={item.name}
              />
              <span className="listing-item-name">{item.name}</span>
            </div>
          ))}
        </div>
      </InfiniteScroll>
    </div>
  )
}

const mapStateToProps = state => {
  return {
    idx: state.listing.idx,
    listingData: state.listing.listingData,
    hasMore: state.listing.hasMore,
  }
}

const mapDispatchToProps = {
  setListingData,
}

export default connect(
  mapStateToProps,
  mapDispatchToProps,
)(ListingPage)
